package com.algorithms.ex;

public class MathEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.abs(23-45));
		System.out.println(Math.abs(23-5));
		System.out.println(Math.floor(23-44));
		System.out.println(Math.floor(23-5.5));
		System.out.println(Math.ceil(23-5.5));
		int value = 4;
		int result = 5;
		result ^= value;
		System.out.println(result);
	}

}
